import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../assets/style.css';

const OrgChart = () => {
    const [employees, setEmployees] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchEmployees = async () => {
            try {
                const response = await axios.get('http://localhost:5070/api/users/orgchart/active');
                setEmployees(response.data);
            } catch (error) {
                console.error('Error fetching active employees', error);
                setError('Error fetching employee data.');
            } finally {
                setLoading(false);
            }
        };

        fetchEmployees();
    }, []);

    const renderEmployee = (employee) => (
        <div key={employee._id} className="org-chart-node">
            <div className="org-chart-node-content">
                <p>{employee.firstName} {employee.lastName}</p>
                <p>{employee.title}</p>
            </div>
        </div>
    );

    const renderHierarchy = (employees, title) => {
        const filteredEmployees = employees.filter(employee => employee.title === title);
        return (
            <div className="org-chart-level">
                {filteredEmployees.map(employee => renderEmployee(employee))}
            </div>
        );
    };

    return (
        <div className="org-chart-container">
            <button onClick={() => navigate('/')}>Home</button>
            <h2>Org Chart</h2>
            <input type="text" placeholder="Email" />
            <button>Search</button>
            {loading && <p>Loading...</p>}
            {error && <p>{error}</p>}
            {!loading && !error && (
                <div className="org-chart">
                    {renderHierarchy(employees, 'CEO')}
                    {renderHierarchy(employees, 'Director')}
                    {renderHierarchy(employees, 'Manager')}
                    {renderHierarchy(employees, 'Staff')}
                </div>
            )}
            <button onClick={() => navigate('/reset')}>Reset</button>
        </div>
    );
};

export default OrgChart;
